Koliko zvezdica će se ispisati ako pozovemo metodu quiz(4); 
void quiz(int i)
{
    if (i > 1)
    {
        quiz(i / 2);
        quiz(i / 2);
    }
    System.out.print("*");
}

Rekurzivna metoda (dat niz int b = {7, 8, 15, 23, 36}) (ako se poziva sa b, 0) 
public int vratiZbir(int[] niz, int index){
    // 2 uslova (1 za return null, drugi za bacanje izuzetka)
    return niz[index] + vratiZbir(niz, index+2);
}
